import React, { useState } from 'react';
import {  useNavigate } from 'react-router-dom';
import { toast } from 'react-toastify';
export default function Login() {
  const [password, setPassword] = useState('');
  const [userName, setUserName] = useState('');
  const [role, setRole] = useState('user'); // Default role is "user"

  const navigate = useNavigate();

  const proceedLoginUsingAPI = (e) => {
    e.preventDefault();

    const apiUrl = role === 'user' ? 'https://localhost:5137/api/Token/Patients' : 'https://localhost:5137/api/Token/Doctor';

    const inputObj = {
      patEmail: userName,
      patPassword: password,
    };

    const inputObj2 = {
      docEmail: userName,
      docPassword: password,
    };

    const input = role === 'user' ? inputObj : inputObj2;

    fetch(apiUrl, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(input),
    })
      .then((res) => res.text())
      .then((resp) => {
        console.log(resp);

        if (resp !== 'Invalid credentials') {
          toast.success('Success');
          localStorage.setItem('token', resp);
          
          if (role === 'user') {
            navigate('/patient'); // Redirect to the "/cake" route after successful user login
          } else if (role === 'admin') {
            navigate('/doctor'); // Redirect to the "/customers" route after successful admin login
          }
        } else {
          toast.error('Invalid credentials');
        }
      })
      .catch((err) => {
        toast.error('Login Failed due to: ' + err.message);
      });
  };
  return (
    <div    className="login-page" style={{ backgroundImage: 'url("https://i.pinimg.com/474x/86/bf/45/86bf45fbbe2718eac587f8ca13841bd7.jpg")', backgroundPosition: 'center', backgroundSize: 'cover' , backdropFilter: 'blur(15px)', minHeight: '100vh', minWidth: '100%' }}>
     <nav className="navbar navbar-expand-lg "style={{ backgroundColor: "#337ab7", color: '#fff' }} >
  <div className="container-fluid">
    <div className="collapse navbar-collapse" id="navbarText">
      <ul className="navbar-nav me-auto mb-2 mb-lg-0">
        <li className="nav-item">
          <a className="nav-link active" aria-current="page" href="">Home</a>
        </li>
        <li className="nav-item">
          <a className="nav-link" href="/doctor">Doctor</a>
        </li>
        <li className="nav-item">
          <a className="nav-link" href="/patient">Patient</a>
        </li>
        <li className="nav-item">
          <a className="nav-link" href="/Admin">Admin</a>
        </li>
      </ul>
      <span className="navbar-text">
        <a className="nav-link" href="/Login">Login</a>
      </span><br/>
      
    </div>
  </div>
  </nav>
    <div className="row justify-content-center"  >
      <div className="col-lg-4" style={{ marginTop: '100px' }} >
        <div className="card" >
         
        </div>
      </div>
      <div className="container">
  <div className="row justify-content-center" style={{ marginTop: '100px' }}>
    <div className="col-lg-4">
      <form onSubmit={proceedLoginUsingAPI}>
        <div className="card">
          <h1 className="text-center">Hi! Login here</h1>
          <div className="card-body">
            <div className="form-group">
              <label style={{ color: '#333333' }}>User Name <span className="errmsg">*</span></label>
              <input
                value={userName}
                onChange={(e) => setUserName(e.target.value)}
                className="form-control"
                style={{ borderColor: '#FFC107' }}
              />
            </div>

            <div className="form-group">
              <label style={{ color: '#333333' }}>Password <span className="errmsg">*</span></label>
              <input
                type="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                className="form-control"
                style={{ borderColor: '#FFC107' }}
              />
            </div>
            
            <div className="form-group">
              <label style={{ color: '#333333' }}>Role <span className="errmsg">*</span></label>
              <div className="form-check">
                <input
                  type="radio"
                  name="role"
                  value="user"
                  checked={role === 'user'}
                  onChange={() => setRole('user')}
                  className="form-check-input"
                />
                <label className="form-check-label">Patient</label>
              </div>
              <div className="form-check">
                <input
                  type="radio"
                  name="role"
                  value="admin"
                  checked={role === 'admin'}
                  onChange={() => setRole('admin')}
                  className="form-check-input"
                />
                <label className="form-check-label">Doctor</label>
              </div>
            </div>
          </div>
          <div className="card-footer text-center">
            <button type="submit" className="btn btn-primary">
              Login
            </button>
          </div>
        </div>
      </form>
    </div>
  </div>
</div>

    </div>
    </div>
  );
}
