import React,{Component}from "react";
import { variables } from "./Variable";
import { toast } from "react-toastify";
import "bootstrap/dist/css/bootstrap.min.css";
import "./App.css";
import axios from 'axios';
export class Doctor extends Component{
    constructor(props) {
        super(props);
        this.state = {
          Doctor: [],
          docName:"",
          docEmail:"",
          docPassword: "",
          speciality: "",
          docAge: 0,
          docGender: "",
          docDescription: "",
          experience: 0,
          docPhoneNumber: 0,
          docImg: "",
          status: "",
          docId: null,
        };
      }
      componentDidMount() {
        this.fetchDoctor();
      }
      fetchDoctor() {
        axios.get(variables.API_URL + 'Doctor', {
          headers: {
            Authorization: 'Bearer ' + localStorage.getItem('token')
          }
        })
          .then((response) => {
            const data = response.data;
            const Doctor = data.map((Doctor) => {
              const { Patient, ...rest } = Doctor;
              return { ...rest, Patient };
            });
      
            this.setState({ Doctor });
          })
          .catch((error) => {
            console.error('Error fetching Doctors:', error);
          });
      }
      handledocInputChange= (event) => {
        this.setState({ docName: event.target.value });
      };
      
      handleimageInputChange= (event) => {
        const file = event.target.files[0]; 
        this.setState({ docImg: file });
      };
      
      handlespecInputChange= (event) => {
        this.setState({ speciality: event.target.value });
      };
      
      handlegenderInputChange= (event) => {
        this.setState({ docGender: event.target.value });
      };
      
      handleexperiInputChange=(event) => {
        this.setState({ experience: event.target.value });
      };
      
      handlepassInputChange=(event) => {
        this.setState({ docPassword: event.target.value });
      };
      
      handlestatusInputChange=(event) => {
        this.setState({ status: event.target.value });
      };      
      //Added things
      handledocEmailInputChange=(event)=>{
        this.setState({docEmail : event.target.value})
      }
      handledocAgeInputChange=(event)=>{
        this.setState({docAge : event.target.value})
      }
      handledocDescriptionInputChange=(event)=>{
        this.setState({docDescription : event.target.value})
      }
      handledocPhoneNumberInputChange=(event)=>{
        this.setState({docPhoneNumber : event.target.value})
      }

      //edit
      
      uhandledocInputChange= (event) => {
        this.setState({ docName: event.target.value });
      };
      
      uhandleimageInputChange= (event) => {
        const file = event.target.files[0]; 
        this.setState({ docImg: file });
      };
      
      uhandlespecInputChange= (event) => {
        this.setState({ speciality: event.target.value });
      };
      
      uhandlegenderInputChange= (event) => {
        this.setState({ docGender: event.target.value });
      };
      
      uhandleexperiInputChange=(event) => {
        this.setState({ experience: event.target.value });
      };
      
      uhandlepassInputChange=(event) => {
        this.setState({ docPassword: event.target.value });
      };
      
      uhandlestatusInputChange=(event) => {
        this.setState({ status: event.target.value });
      };      
      //Added things
      uhandledocEmailInputChange=(event)=>{
        this.setState({docEmail : event.target.value})
      }
      uhandledocAgeInputChange=(event)=>{
        this.setState({docAge : event.target.value})
      }
      uhandledocDescriptionInputChange=(event)=>{
        this.setState({docDescription : event.target.value})
      }
      uhandledocPhoneNumberInputChange=(event)=>{
        this.setState({docPhoneNumber : event.target.value})
      }
      createItem = () => {
        const { docName, speciality, docGender,experience,docPassword, docImg, docEmail, docAge, docDescription, docPhoneNumber } = this.state;
        const formData = new FormData();
        formData.append("docName", docName);
        formData.append("speciality", speciality);
        formData.append("docGender", docGender);
        formData.append("experience", experience);
        formData.append("docPassword", docPassword);
        formData.append("imageFile", docImg);
        formData.append("docEmail", docEmail);
        formData.append("docAge", docAge);
        formData.append("docDescription", docDescription);
        formData.append("docPhoneNumber", docPhoneNumber);
      
        fetch("https://localhost:5137/api/Doctor", {
          method: "POST",
          headers: {
            Authorization: "Bearer " + localStorage.getItem("token"),
          },
          body: formData,
        })
          .then((response) => {
            if (!response.ok) {
              throw new Error("Failed to create the cake. HTTP status " + response.status);
            }
            return response.json();
          })
          .then((data) => {
            console.log("Doctor Created:", data);
            this.fetchDoctor();
            this.setState({
              docName: "",
              speciality: "",
              experience: 0,
              docGender:"",
              docPassword:"",
              docImg: null,
              docEmail:"",
              docAge:0,
              docDescription:"",
              docPhoneNumber:0
            });
          })
          .catch((error) => {
            console.error("Error Creating the Doctor:", error);
          });
      };
      editItem = () => {
        const { docId,docName, speciality, docGender,experience,docPassword, docImg, docEmail, docAge, docDescription, docPhoneNumber  } = this.state;
      
        const formData = new FormData();
        formData.append('docId', docId);
        formData.append('docName', docName);
        formData.append('doc', 'doctor value'); 
        formData.append('speciality', speciality);
        formData.append('docGender', docGender);
        formData.append('experience', experience);
        formData.append('docPassword', docPassword);
        formData.append('imageFile', docImg);
        formData.append("docEmail", docEmail);
        formData.append("docAge", docAge);
        formData.append("docDescription", docDescription);
        formData.append("docPhoneNumber", docPhoneNumber);
      
        axios.put(variables.API_URL + `Doctor/${docId}`, formData, {
          headers: {
            Authorization: 'Bearer ' + localStorage.getItem('token'),
            'Content-Type': 'multipart/form-data',
          },
        })
          .then((response) => response.data)
          .then((data) => {
            console.log('Doctor Updated:', data);
            this.fetchDoctor();
            this.setState({
              doctor_id: 0,
              docName: '',
              speciality: '',
              docGender: "",
              experience:0,
              docPassword:"",
              docImg: null,
              docEmail:"",
              docAge:0,
              docDescription:"",
              docPhoneNumber:0
            });
      
            // Update the image source
            const imageElement = document.getElementById('docImg');
            if (imageElement) {
              imageElement.src = data.docImg;
            }
          })
          .catch((error) => {
            console.error('Error Updating the Doctor:', error);
          });
      };
      deleteItem = (docId) => {
        fetch(variables.API_URL + `Doctor/${docId}`, {
          method: "DELETE",
          headers: {
            Authorization: "Bearer " + localStorage.getItem("token"),
          },
        })
          .then((response) => response.json())
          .then((data) => {
            console.log("Doctor Deleted:", data);
            this.fetchDoctor();
          })
          .catch((error) => {
            console.error("Error Deleting the Doctor:", error);
          });
      };
    
    render(){
        const {
            docId,
            Doctor,
            docImg,
            docName,
            speciality,
            docGender,
            experience,
            docPassword,
            status,
            docEmail,
            docAge,
            docDescription,
            docPhoneNumber
           
          } = this.state;
      
  return(
       <div className="container">
      <h2 className="mb-4">Doctor List</h2>

      <div className="row">
        {Doctor.map((doc) => (
          <div className="col-md-4 mb-4" key={doc.docId}>
            <div className="card card-small">
            <img
        src={`https://localhost:5137/Uploads/${doc.docImg}`}
        className="card-img-top doctor-image"
        alt={doc.docName}
      />
              <div className="card-body">
                <h5 className="card-title">{doc.docName}</h5>
                <p className="card-text">Specialization: {doc.speciality}</p>
                <p className="card-text">Doctor Gender: {doc.docGender}</p>
                <p className="card-text">Doctor Experience: {doc.experience}</p>
                <p className="card-text">{doc.docDescription}</p>
                <div className="btn-group" role="group">
                <td> <button type="button"
                      className="btn btn"
                      onClick={() =>
                        this.setState({docId: doc.docId, docName: doc.docName,docImg:doc.docImg,speciality:doc.speciality,experience:doc.experience,docGender:doc.docGender,docEmail:doc.docEmail,docAge:doc.docAge,docPhoneNumber:doc.docPhoneNumber,docDescription:doc.docDescription,docImg:doc.docImg })
                      }><svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" className="bi bi-pencil-square" viewBox="0 0 16 16">
                          <path d="M15.502 1.94a.5.5 0 0 1 0 .706L14.459 3.69l-2-2L13.502.646a.5.5 0 0 1 .707 0l1.293 1.293zm-1.75 2.456-2-2L4.939 9.21a.5.5 0 0 0-.121.196l-.805 2.414a.25.25 0 0 0 .316.316l2.414-.805a.5.5 0 0 0 .196-.12l6.813-6.814z"/>
                          <path fillRule="evenodd" d="M1 13.5A1.5 1.5 0 0 0 2.5 15h11a1.5 1.5 0 0 0 1.5-1.5v-6a.5.5 0 0 0-1 0v6a.5.5 0 0 1-.5.5h-11a.5.5 0 0 1-.5-.5v-11a.5.5 0 0 1 .5-.5H9a.5.5 0 0 0 0-1H2.5A1.5 1.5 0 0 0 1 2.5v11z"/>
                          </svg> </button> </td>
                  <td><button type="button"
                      className="btn btn" onClick={() => this.deleteItem(doc.docId)}> 
                      <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" className="bi bi-trash-fill" viewBox="0 0 16 16">
                          <path d="M2.5 1a1 1 0 0 0-1 1v1a1 1 0 0 0 1 1H3v9a2 2 0 0 0 2 2h6a2 2 0 0 0 2-2V4h.5a1 1 0 0 0 1-1V2a1 1 0 0 0-1-1H10a1 1 0 0 0-1-1H7a1 1 0 0 0-1 1H2.5zm3 4a.5.5 0 0 1 .5.5v7a.5.5 0 0 1-1 0v-7a.5.5 0 0 1 .5-.5zM8 5a.5.5 0 0 1 .5.5v7a.5.5 0 0 1-1 0v-7A.5.5 0 0 1 8 5zm3 .5v7a.5.5 0 0 1-1 0v-7a.5.5 0 0 1 1 0z"/>
                          </svg></button></td>
                </div>
              </div>
            </div>
          </div>
        ))}
      </div>
      <div className="card mb-4 card-sm">
        <div className="card-body">
          <h5 className="card-title">Create New Doctor</h5>
          <div className="form-group">
            <label htmlFor="docName">Name:</label>
            <input
              type="text"
              className="form-control"
              id="docName"
              value={docName}
              onChange={this.handledocInputChange}
            />
          </div>
          <div className="form-group">
            <label htmlFor="docEmail">Email:</label>
            <input
              type="text"
              className="form-control"
              id="docEmail"
              value={docEmail}
              onChange={this.handledocEmailInputChange}
            />
          </div>
          <div className="form-group">
            <label htmlFor="docPassword">Password:</label>
            <input
              type="password"
              className="form-control"
              id="docPassword"
              value={docPassword}
              onChange={this.handlepassInputChange}
            />
          </div>
          <div className="form-group">
            <label htmlFor="docPassword">Age:</label>
            <input
              type="number"
              className="form-control"
              id="docPassword"
              value={docAge}
              onChange={this.handledocAgeInputChange}
            />
            </div>
            <div className="form-group">
            <label htmlFor="docGender">Gender:</label>
            <input
              type="text"
              className="form-control"
              id="docGender"
              value={docGender}
              onChange={this.handlegenderInputChange}
            />
          </div>
            <div className="form-group">
            <label htmlFor="speciality">Specialization</label>
            <input
              type="text"
              className="form-control"
              id="speciality"
              value={speciality}
              onChange={this.handlespecInputChange}
            />
          </div>
          <div className="form-group">
            <label htmlFor="experience">Experience:</label>
            <input
              type="number"
              className="form-control"
              id="experience"
              value={experience}
              onChange={this.handleexperiInputChange}
            />
          </div>
          <div className="form-group">
            <label htmlFor="experience">Contact Number:</label>
            <input
              type="number"
              className="form-control"
              id="experience"
              value={docPhoneNumber}
              onChange={this.handledocPhoneNumberInputChange}
            />
          </div>
          <div className="form-group">
            <label htmlFor="experience">About You:</label>
            <input
              type="text"
              className="form-control"
              id="description"
              value={docDescription}
              onChange={this.handledocDescriptionInputChange}
            />
          </div>
          <div className="form-group">
            <label htmlFor="docImg">Picture:</label>
            <input
              type="file"
              className="form-control"
              id="docImg"
              onChange={this.handleimageInputChange}
            />
          </div>
          <br></br>
          <button className="btn btn-primary" onClick={this.createItem}>
            Create Doctor
          </button>
        </div>
      </div>
      {/*create over*/}
      <div className="card card-sm">
              <div className="card-body">
                <h5 className="card-title">Edit Doctor</h5>
                <div className="form-group">
                  <label htmlFor="doctor_id">Doctor Id:</label>
                  <input
                    type="text"
                    className="form-control"
                    id="docId"
                    value={docId}
                    onChange={this.handledocIdInputChange}
                  />
                </div>
                <div className="form-group">
                  <label htmlFor="docName">Name:</label>
                  <input
                    type="text"
                    className="form-control"
                    id="docName"
                    value={docName}
                    onChange={this.uhandledocInputChange}
                  />
                </div>
                <div className="form-group">
                  <label htmlFor="docEmail">Email:</label>
                  <input
                    type="text"
                    className="form-control"
                    id="docEmail"
                    value={docEmail}
                    onChange={this.uhandledocEmailInputChange}
                  />
                </div>
                <div className="form-group">
                  <label htmlFor="docPassword">Password:</label>
                  <input
                    type="text"
                    className="form-control"
                    id="docPassword"
                    value={docPassword}
                    onChange={this.uhandlepassInputChange}
                  />
                </div>
                <div className="form-group">
                <label htmlFor="docPassword">Age:</label>
            <input
              type="number"
              className="form-control"
              id="docPassword"
              value={docAge}
              onChange={this.uhandledocAgeInputChange}
            />
            </div>
            <div className="form-group">
                  <label htmlFor="docGender">Gender:</label>
                  <input
                    type="text"
                    className="form-control"
                    id="docGender"
                    value={docGender}
                    onChange={this.uhandledocgenInputChange}
                  />
                </div>
                <div className="form-group">
                  <label htmlFor="speciality">Specialization:</label>
                  <input
                    type="text"
                    className="form-control"
                    id="speciality"
                    value={speciality}
                    onChange={this.handledocspecInputChange}
                  />
                </div>
                <div className="form-group">
                  <label htmlFor="experience">Experience:</label>
                  <input
                    type="number"
                    className="form-control"
                    id="experience"
                    value={experience}
                    onChange={this.handledocexperiInputChange}
                  />
                </div>
                <div className="form-group">
            <label htmlFor="experience">Contact Number:</label>
            <input
              type="number"
              className="form-control"
              id="experience"
              value={docPhoneNumber}
              onChange={this.handledocPhoneNumberInputChange}
            />
          </div>
          <div className="form-group">
            <label htmlFor="experience">About You:</label>
            <input
              type="text"
              className="form-control"
              id="description"
              value={docDescription}
              onChange={this.handledocDescriptionInputChange}
            />
          </div>
                <div className="form-group">
                  <label htmlFor="docImg">Picture:</label>
                  <input
                    type="file"
                    className="form-control"
                    id="docImg"
                    onChange={this.handledocImageeditInputChange}
                  />
                </div>
                <br></br>
                {/* Button to trigger the editItem function */}
                <button className="btn btn-primary" onClick={this.editItem}>
                  Update Doctor
                </button>
              </div>
            </div>
      </div>
        )
    }
}
