import React, { Component } from "react";
import axios from "axios";
import { variables } from "./Variable";
import { ToastContainer } from "react-toastify";

export class Patient extends Component  {
  constructor(props) {
    super(props);
    this.state = {
      Patient: [],
      patId: 0,
      docId: 0,
      doctors: null,
      patName: "",
      age: 0,
      patGender: "",
      patIssue: "",
      patPhoneNumber: "",
      patient_Address: "",
      patEmail: "",
      patPassword: ""

    };
  }

  componentDidMount() {
    this.fetchPatients();
  }

  fetchPatients() {
    axios
      .get("https://localhost:5137/api/Patient", {
        headers: {
          Authorization: 'Bearer ' + localStorage.getItem('token')
        }
      })
      .then((response) => {
        const Patient = response.data;
        this.setState({ Patient});
      })
      .catch((error) => {
        console.error("Error fetching Patient:", error);
      });
  }

  handleInputChange = (event) => {
    this.setState({ [event.target.name]: event.target.value });
  };

  createPatient = () => {
    const {
      docId,
      patName,
      age,
      patGender,
      patIssue,
      patPhoneNumber,    
      patient_Address,
      patEmail,
      patPassword
    } = this.state;

    const patient = {
      docId,
      patName,
      age,
      patGender,
      patIssue,
      patPhoneNumber,
      patient_Address,
      patEmail,
      patPassword
    
    };
   

    axios
      .post("https://localhost:5137/api/Patient",patient, {
        headers: {
          Authorization: 'Bearer ' + localStorage.getItem('token')
        }
      })
      .then((response) => {
        console.log("Patient created:", response.data);
        this.fetchPatients();
        this.resetForm();
      })
      .catch((error) => {
        console.error("Error creating patient:", error);
      });
  };

  updatePatient = () => {
    const {
      patId,
      docId,
      patName,
      age,
      patGender,
      patIssue,
      patPhoneNumber,
      patEmail,
      patPassword
     
    } = this.state;

    const patient = {
      docId,
      patId,
      patName,
      age,
      patGender,
      patIssue,
      patPhoneNumber,
      patEmail,
      patPassword,
    };


    axios
      .put(`https://localhost:5137/api/Patient/${patId}`, patient, {
        headers: {
          Authorization: 'Bearer ' + localStorage.getItem('token')
        }
      })
      .then((response) => {
        console.log("Patient updated:", response.data);
        this.fetchPatients();
        this.resetForm();
      })
      .catch((error) => {
        console.error("Error updating patient:", error);
      });
  };

  deletePatient = (patId) => {
    axios
      .delete(`https://localhost:5137/api/Patient/${patId}`, {
        headers: {
          Authorization: 'Bearer ' + localStorage.getItem('token')
        }
      })
      .then((response) => {
        console.log("Patient deleted:", response.data);
        this.fetchPatients();
      })
      .catch((error) => {
        console.error("Error deleting patient:", error);
      });
  };

  resetForm = () => {
    this.setState({
      patId: 0,
      docId: 0,
      doctor: null,
      patName: "",
      age: 0,
      patGender: "",
      patIssue:"",
      patPhoneNumber: "", 
      patEmail: "",
      patPassword: ""
      
    });
  };

  render() {
    const {
      Patient,
      patId,
      docId,
      patName,
      age,
      patGender,
      patIssue,
      patPhoneNumber,
      patEmail,
      patPassword
    
    } = this.state;


return (
  <div>
    <ToastContainer theme='colored'></ToastContainer>
    <nav className="navbar navbar-expand-lg "style={{ backgroundColor: "#337ab7", color: '#fff' }} >
  <div className="container-fluid">
    <div className="collapse navbar-collapse" id="navbarText">
      <ul className="navbar-nav me-auto mb-2 mb-lg-0">
        <li className="nav-item">
          <a className="nav-link active" aria-current="page" href="/Homepg">Home</a>
        </li>
        <li className="nav-item">
          <a className="nav-link" href="/doctor">Doctor</a>
        </li>
        <li className="nav-item">
          <a className="nav-link" href="/patient">Patient</a>
        </li>
        <li className="nav-item">
          <a className="nav-link" href="/Admin">Admin</a>
        </li>
      </ul>
      <span className="navbar-text">
        <a className="nav-link" href="/Login">Login</a>
      </span><br/>
      
    </div>
  </div>
  </nav>
  <div className="container">
    <h2>Patients</h2>

    <div>
      <h3>Add/Edit Patient</h3>
      <form>
      {/* <label htmlFor="patID" className="form-label">
            Patient ID
          </label>
          <input
            type="text"
            className="form-control"
            id="patId"
            name="patId"
            //value={patId}
            onChange={this.handleInputChange}
          /> */}
        <div className="mb-3">
          <label htmlFor="docId" className="form-label">
            Doctor ID
          </label>
          <input
            type="text"
            className="form-control"
            id="docId"
            name="docId"
            value={docId}
            onChange={this.handleInputChange}
          />
        </div>
        <div className="mb-3">
          <label htmlFor="age" className="form-label">
            Age
          </label>
          <input
            type="number"
            className="form-control"
            id="age"
            name="age"
            value={age}
            onChange={this.handleInputChange}
          />
        </div>
        <div className="mb-3">
          <label htmlFor="patGender" className="form-label">
          patGender
          </label>
          <input
            type="text"
            className="form-control"
            id="patGender"
            name="patGender"
            value={patGender}
            onChange={this.handleInputChange}
          />
        </div>

        <div className="mb-3">
          <label htmlFor="patIssue" className="form-label">
          Health Issue
          </label>
          <input
            type="text"
            className="form-control"
            id="patIssue"
            name="patIssue"
            value={patIssue}
            onChange={this.handleInputChange}
          />
        </div>
        <div className="mb-3">
          <label htmlFor="patPhoneNumber" className="form-label">
          phone number
          </label>
          <input
            type="text"
            className="form-control"
            id="patPhoneNumber"
            name="patPhoneNumber"
            value={patPhoneNumber}
            onChange={this.handleInputChange}
          />
        </div>
        <div className="mb-3">
          <label htmlFor="patEmail" className="form-label">
          User Email
          </label>
          <input
            type="text"
            className="form-control"
            id="patEmail"
            name="patEmail"
            value={patEmail}
            onChange={this.handleInputChange}
          />
        </div>
        <div className="mb-3">
          <label htmlFor="patName" className="form-label">
            User name
          </label>
          <input
            type="text"
            className="form-control"
            id="patName"
            name="patName"
            value={patName}
            onChange={this.handleInputChange}
          />
        </div>
         <div className="mb-3">
          <label htmlFor="patPassword" className="form-label">
      User password
          </label>
          <input
            type="text"
            className="form-control"
            id="patPassword"
            name="patPassword"
            value={patPassword}
            onChange={this.handleInputChange}
          />
        </div>

        {patId === 0 ? (
          <button type="button" className="btn btn-primary" onClick={this.createPatient}>
            Add Patient
          </button>
        ) : (
          <button type="button" className="btn btn-primary" onClick={this.updatePatient}>
            Update Patient
          </button>
        )}

        <button type="button" className="btn btn-secondary" onClick={this.resetForm}>
          Cancel
        </button>
      </form>
    </div>
<div>
  <a href="/Registerpat">patient</a>
</div>
    <div>
      <h3>Patients List</h3>
      <table className="table">
        <thead>
          <tr>
            <th>Patient Id</th>
            <th>Patient Name</th>
            <th>Patient Age</th>
            <th>Gender</th>
            <th>Patient Issue</th>
            <th>Phone Number</th>
            <th>Username</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {Patient.map((patient) => (
            <tr key={patient.patId}>
              <td>{patient.patId}</td>
              <td>{patient.patName}</td>
              <td>{patient.age}</td>
              <td>{patient.patGender}</td>
              <td>{patient.patIssue}</td>
              <td>{patient.patPhoneNumber}</td>
              <td>{patient.patEmail}</td>
              <td>
                <button
                  type="button"
                  className="btn btn-danger"
                  onClick={() => this.deletePatient(patient.patId)}
                >
                  Delete
                </button>
                <button
                  type="button"
                  className="btn btn-primary"
                  onClick={() => this.setState(patient)}
                >
                  Update
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  </div>
  </div>
);
}
}
